binding.script:
                    var lever = getprop("/controls/gear/brake-parking");

                    if (arg[0]) {
                        var text = [' (tap brakes to release)', ' (push lever back!)'];
                        return sprintf('ENABLED%s', text[lever]);
                    }
                    else {
                        var text = ['', ' (depress and release brakes)'];
                        return sprintf('RELEASED%s', text[lever]);
                    }
